"use strict";

var _ = require(".");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('base64Validate', () => {
  it('validates a mixed base64 utf8 string', () => {
    expect(() => (0, _.base64Validate)('aGVsbG8gd29ybGQg0J/RgNC40LLQtd^GC0YHRgtCy0YPRjiDQvNC4IOS9oOWlvQ==')).toThrow(/Invalid base64 encoding/);
  });
});