"use strict";

var _ = require(".");

// Copyright 2017-2020 @polkadot/util authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('filterArray', () => {
  it('filters arrays, removing undefined', () => {
    expect((0, _.arrayFilter)([0, '', null, false, undefined, NaN])).toEqual([0, '', null, false, NaN]);
  });
  it('filters arrays, removing undefined & null (allowNull = false)', () => {
    expect((0, _.arrayFilter)([0, '', null, false, undefined, NaN], false)).toEqual([0, '', false, NaN]);
  });
});