"use strict";

var _ = require(".");

// Copyright 2017-2020 @polkadot/util authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('extractTime', () => {
  const milliseconds = 1e9 + 123;
  it('extracts time components correctly', () => {
    expect((0, _.extractTime)(milliseconds)).toEqual({
      days: 11,
      hours: 13,
      milliseconds: 123,
      minutes: 46,
      seconds: 40
    });
  });
});