"use strict";

var _ = require(".");

// Copyright 2017-2020 @polkadot/util authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('isBigInt', () => {
  it('returns true when a BigInt value', () => {
    expect((0, _.isBigInt)(123456n)).toEqual(true);
  });
  it('returns true when a BigInt value', () => {
    expect((0, _.isBigInt)(BigInt(123456))).toEqual(true);
  });
  it('returns false on non-BigInt values', () => {
    expect((0, _.isBigInt)(0)).toEqual(false);
  });
});