describe('isFunction', () => {
  it('returns true on valid functions', () => {
    expect((0, _.isFunction)(_.isFunction)).toEqual(true);
  });
  it('returns false on invalid functions', () => {
    expect((0, _.isFunction)('notAFunction')).toEqual(false);
  });
});